import { report } from '../index.js';
export const outputTodos = async (todos, options) => {
    try {
        const output = await report(todos, options.reporter);
        console.log(output);
    }
    catch (e) {
        console.error(e);
    }
    if (options.exitNicely) {
        process.exit(0);
    }
    process.exit(todos.length ? 1 : 0);
};
